/// &lt;summary&gt; /// User session store /// &lt;/summary&gt; public interface IUserSessionStore { /// &lt;summary&gt; /// Retrieves a user session /// &lt;/summary&gt; /// &lt;param name=&#34;key&#34;&gt;&lt;/param&gt; /// &lt;param name=&#34;cancellationToken&#34;&gt;A token that can be used to request cancellation of the asynchronous operation.&lt;/param&gt; /// &lt;returns&gt;&lt;/returns&gt; Task&lt;UserSession?&gt; GetUserSessionAsync(string key, CancellationToken cancellationToken = default); /// &lt;summary&gt; /// Creates a user session /// &lt;/summary&gt; /// &lt;param name=&#34;session&#34;&gt;&lt;/param&gt; /// &lt;param name=&#34;cancellationToken&#34;&gt;A token that can be used to request cancellation of the asynchronous operation.&lt;/param&gt; /// &lt;returns&gt;&lt;/returns&gt; Task CreateUserSessionAsync(UserSession session, CancellationToken cancellationToken = default); /// &lt;summary&gt; /// Updates a user session /// &lt;/summary&gt; /// &lt;param name=&#34;key&#34;&gt;&lt;/param&gt; /// &lt;param name=&#34;session&#34;&gt;&lt;/param&gt; /// &lt;param name=&#34;cancellationToken&#34;&gt;A token that can be used to request cancellation of the asynchronous operation.&lt;/param&gt; /// &lt;returns&gt;&lt;/returns&gt; Task UpdateUserSessionAsync(string key, UserSessionUpdate session, CancellationToken cancellationToken = default); /// &lt;summary&gt; /// Deletes a user session /// &lt;/summary&gt; /// &lt;param name=&#34;key&#34;&gt;&lt;/param&gt; /// &lt;param name=&#34;cancellationToken&#34;&gt;A token that can be used to request cancellation of the asynchronous operation.&lt;/param&gt; /// &lt;returns&gt;&lt;/returns&gt; Task DeleteUserSessionAsync(string key, CancellationToken cancellationToken = default); /// &lt;summary&gt; /// Queries user sessions based on the filter. /// &lt;/summary&gt; /// &lt;param name=&#34;filter&#34;&gt;&lt;/param&gt; /// &lt;param name=&#34;cancellationToken&#34;&gt;A token that can be used to request cancellation of the asynchronous operation.&lt;/param&gt; /// &lt;returns&gt;&lt;/returns&gt; Task&lt;IReadOnlyCollection&lt;UserSession&gt;&gt; GetUserSessionsAsync(UserSessionsFilter filter, CancellationToken cancellationToken = default); /// &lt;summary&gt; /// Deletes user sessions based on the filter. /// &lt;/summary&gt; /// &lt;param name=&#34;filter&#34;&gt;&lt;/param&gt; /// &lt;param name=&#34;cancellationToken&#34;&gt;A token that can be used to request cancellation of the asynchronous operation.&lt;/param&gt; /// &lt;returns&gt;&lt;/returns&gt; Task DeleteUserSessionsAsync(UserSessionsFilter filter, CancellationToken cancellationToken = default); } Once you have an implementation, you can register it when you enable server-side sessions:
builder.Services.AddBff() .AddServerSideSessions&lt;YourStoreClassName&gt;(); User Session Store Cleanup The IUserSessionStoreCleanup interface is used to model cleaning up expired sessions.
/// &lt;summary&gt; /// User session store cleanup /// &lt;/summary&gt; public interface IUserSessionStoreCleanup { /// &lt;summary&gt; /// Deletes expired sessions /// &lt;/summary&gt; Task DeleteExpiredSessionsAsync(CancellationToken cancellationToken = default); }

